    private void hypButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hypButtonActionPerformed
        if(!ShiftTextdisplay.getText().equals("SHIFT") && !HypTextdisplay.getText().equals("Hyp")){
            //this will show the Hyp text
            HypTextdisplay.setText("Hyp");
            
            
            //this will make visible the Hyperbolic action keys
            TrigonomerticPanel.removeAll();
            TrigonomerticPanel.setSize(PressedHyp.getSize());
            TrigonomerticPanel.add(PressedHyp);
            TrigonomerticPanel.repaint();
            TrigonomerticPanel.revalidate();
            
        }
        //If shift is pressed once, it will show remove Hyperbolic action
        else{
            ShiftTextdisplay.setText("");
            HypTextdisplay.setText("");
            
            //this will remove all buttons that appeared after pressing the Hyperbolic key
            TrigonomerticPanel.removeAll();
            TrigonomerticPanel.setSize(DefPanel2.getSize());
            TrigonomerticPanel.add(DefPanel2);
            TrigonomerticPanel.repaint();
            TrigonomerticPanel.revalidate();
            
            ShiftKeyPanel.removeAll();
            ShiftKeyPanel.setSize(DefPanel1.getSize());
            ShiftKeyPanel.add(DefPanel1);
            ShiftKeyPanel.repaint();
            ShiftKeyPanel.revalidate();
        }
    }//GEN-LAST:event_hypButtonActionPerformed

