    private void shiftButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_shiftButtonActionPerformed
        //If shift is not pressed before, it will show SHIFT message above the display and do shift actions
        if(!ShiftTextdisplay.getText().equals("SHIFT") && !HypTextdisplay.getText().equals("Hyp")){
            //this will show the SHIFT text
            ShiftTextdisplay.setText("SHIFT");
            
            
            //this will make visible the shift action keys
            ShiftKeyPanel.removeAll();
            ShiftKeyPanel.setSize(PressedShift1.getSize());
            ShiftKeyPanel.add(PressedShift1);
            ShiftKeyPanel.repaint();
            ShiftKeyPanel.revalidate();
            
            TrigonomerticPanel.removeAll();
            TrigonomerticPanel.setSize(PressedShift2.getSize());
            TrigonomerticPanel.add(PressedShift2);
            TrigonomerticPanel.repaint();
            TrigonomerticPanel.revalidate();
            
        }
        //If shift is pressed once, it will show remove SHIFT action
        else{
            ShiftTextdisplay.setText("");
            HypTextdisplay.setText("");
            
            //this will remove all buttons that appeared after pressing the shift key
            ShiftKeyPanel.removeAll();
            ShiftKeyPanel.setSize(DefPanel1.getSize());
            ShiftKeyPanel.add(DefPanel1);
            ShiftKeyPanel.repaint();
            ShiftKeyPanel.revalidate();
            
            TrigonomerticPanel.removeAll();
            TrigonomerticPanel.setSize(DefPanel2.getSize());
            TrigonomerticPanel.add(DefPanel2);
            TrigonomerticPanel.repaint();
            TrigonomerticPanel.revalidate();
        }
    }//GEN-LAST:event_shiftButtonActionPerformed
    
